import React, { useState, useEffect } from 'react';
import WelcomeScreen from './WelcomeScreen';
import Survey from './Survey';
import ThankYouScreen from './ThankYouScreen';
import './admin.css'; // Add CSS for admin view if necessary

const App = () => {
  const [started, setStarted] = useState(false);
  const [completed, setCompleted] = useState(false);
  const [viewAdmin, setViewAdmin] = useState(false); // State to toggle admin view

  const handleStart = () => {
    setStarted(true);
  };

  const handleCompletion = () => {
    setCompleted(true);
  };

  const resetSurvey = () => {
    setStarted(false);
    setCompleted(false);
    localStorage.removeItem('surveyAnswers');
  };

  const [surveyData, setSurveyData] = useState([]);

  useEffect(() => {
    if (viewAdmin) {
      const storedData = JSON.parse(localStorage.getItem('surveyAnswers')) || [];
      setSurveyData(storedData);
    }
  }, [viewAdmin]);

  const renderAdminPage = () => (
    <div className="admin-page">
      <h2>Survey Responses</h2>
      {surveyData.length === 0 ? (
        <p>No survey responses found.</p>
      ) : (
        <table>
          <thead>
            <tr>
              <th>Session ID</th>
              <th>Responses</th>
            </tr>
          </thead>
          <tbody>
            {surveyData.map((session, index) => (
              <tr key={index}>
                <td>{session.sessionId}</td>
                <td>
                  {Object.entries(session.answers).map(([questionId, answer], idx) => (
                    <div key={idx}>
                      <strong>Question {questionId}:</strong> {answer}
                    </div>
                  ))}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
      <button onClick={() => setViewAdmin(false)}>Back to Survey</button>
    </div>
  );

  if (viewAdmin) {
    return renderAdminPage();
  }

  if (completed) {
    return <ThankYouScreen onReset={resetSurvey} />;
  }

  return (
    <div>
      <button onClick={() => setViewAdmin(!viewAdmin)}>
        {viewAdmin ? 'Back to Survey' : 'View Admin Page'}
      </button>
      {started ? (
        <Survey onComplete={handleCompletion} />
      ) : (
        <WelcomeScreen onStart={handleStart} />
      )}
    </div>
  );
};

export default App;
