import React, { useState } from 'react';
import WelcomeScreen from './WelcomeScreen';
import Survey from './Survey';
import ThankYouScreen from './ThankYouScreen';
AdminPage

const App = () => {
  const [started, setStarted] = useState(false);
  const [completed, setCompleted] = useState(false);
  const [viewAdmin, setViewAdmin] = useState(false); // State to toggle admin view

  const handleStart = () => {
    setStarted(true);
  };

  const handleCompletion = () => {
    setCompleted(true);
  };

  const resetSurvey = () => {
    setStarted(false);
    setCompleted(false);
    localStorage.removeItem('surveyAnswers');
  };

  if (viewAdmin) {
    return <AdminPage />; // Render the AdminPage when the button is clicked
  }

  if (completed) {
    return <ThankYouScreen onReset={resetSurvey} />;
  }

  return (
    <div>
      <button onClick={() => setViewAdmin(!viewAdmin)}>
        {viewAdmin ? 'Back to Survey' : 'View Admin Page'}
      </button>
      {started ? (
        <Survey onComplete={handleCompletion} />
      ) : (
        <WelcomeScreen onStart={handleStart} />
      )}
    </div>
  );
};

export default App;
